import AsyncStorage from '@react-native-async-storage/async-storage';

// armazena o nome de usuário e senha em AsyncStorage
export const storeUserData = async (username, password, token) => {
    const currentTime = new Date().getTime();
    
    try {
        await AsyncStorage.setItem('username', username);
        await AsyncStorage.setItem('password', password);
        await AsyncStorage.setItem('token', token);
        await AsyncStorage.setItem('loginTime', currentTime.toString());

        console.log("Credenciais salvas com sucesso");
        console.log(`${username}\n${password}\n${token}\n${currentTime.to}`)
    } catch (error) {
        console.log("Erro ao salvar credenciais");
        console.log(error);
    }

    clearUserData()
};

// recupera o nome de usuário e senha de AsyncStorage
export const getUserData = async () => {
    try {
        const username = await AsyncStorage.getItem('username');
        const password = await AsyncStorage.getItem('password');

            const credenciais = {
                "login": username,
                "password": password,

            }

            console.log("login" +username + "\n" +
                "password" + password + "\n")

            return credenciais

    } catch (error) {
        console.log("erro " + error);
    }
};

export const clearUserData = async () => {
    try {
        await AsyncStorage.removeItem('username');
        await AsyncStorage.removeItem('password');
        await AsyncStorage.removeItem('token');
        await AsyncStorage.removeItem('loginTime');
    } catch (error) {
        console.log(error);
    }
};

export const isUserLoggedFor24Hours = async () => {
    try {
      // recupera o horário de entrada salvo no AsyncStorage
      const loginTime = await AsyncStorage.getItem('loginTime');
      if (!loginTime) {
        // usuário nunca logou
        return false;
      }
  
      // calcula a diferença entre o horário atual e o horário de entrada em horas
      const currentTime = new Date().getTime();
      const loginTimeInMillis = parseInt(loginTime);
      const hoursSinceLogin = (currentTime - loginTimeInMillis) / 1000 / 60 / 60;
  
      // verifica se a diferença é maior que 24 horas
      return hoursSinceLogin >= 24;
    } catch (error) {
      console.log(error);
      return false;
    }
  };

